import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http';
import * as jsPDF from 'jspdf';

@Injectable({
  providedIn: 'root'
})
export class ReportService 
{
  data:any[];
  request_URL:string = 'http://localhost:3000/pastries'; //get pastry report

  /*IMAGES*/
  pastryLogo_dataURL:string = "data:image/png;base64,"
  
  constructor(private httpClient:HttpClient)
  {
  }

  public generateReport(id:number)
  {
    this.httpClient.get(`${this.request_URL}/${id}`).subscribe(res=>
      {
        this.data = res as any []; //get the db response
        var doc = new jsPDF();
        doc.setFontSize(40);
        doc.text(35, 25, this.data[0].p_name);
        doc.addImage(this.pastryLogo_dataURL, 'PNG', 50, 35, 100, 100);
        doc.setFontSize(20);
        doc.text(35, 140,`category: ${this.data[0].c_name}`);
        doc.text(35, 150, `Price ${this.data[0].p_price}$`);
        doc.setFontSize(13);
        doc.text(35,160,` ${this.data[0].p_desc}`);
        doc.save('pastry.pdf');
      });
  }

}
